/**
 * `useCoords` is a thin wrapper around Google's Geocoding API. It provides a
 * single `getCoords` method that takes a partial `Address` and returns a
 * `Coords[]` array with `lat` and `lng`.
 *
 * Requires a `<GeoProvider />` context with valid API keys.
 *
 * Example
 *
 * ```
 * const App = (props) => {
 *   const { coords, getCoords } = useCoords();
 *
 *   useEffect(() => {
 *     getCoords(props.address);
 *   }. [getCoords]);
 *
 *   return (
 *     <p>Lat: {coords[0]?.lat}, Lng: {coords[0]?.lng}</p>
 *   );
 * };
 * ```
 */
import { Address, Coords } from "providers/GeoProvider";
declare const mockCoords: Coords[];
declare const useCoords: () => {
    coords: Coords[] | null;
    getCoords: (address: Partial<Address>) => Promise<Coords[]>;
};
export default useCoords;
export type { Coords };
export { mockCoords };
