/**
 * `useHover` detects whether the mouse is hovering an element by returning a
 * `ref` you can pass to a target element and a `isHovered` boolean.
 *
 * Example
 *
 * ```
 * const App = () => {
 *   const hoverRef = useRef();
 *   const isHovered = useHover(hoverRef);
 *
 *   return (
 *     <button ref={hoverRef}>
 *       {isHovered ? "Hovered" : "Hover me"}
 *     </div>
 *   );
 * };
 * ```
 *
 * Credit: https://usehooks.com/useHover/
 */
import { RefObject } from "react";
declare const useHover: <T extends Element>(element: RefObject<T>) => boolean;
export default useHover;
