/**
 * `useTimer` is a simple timer hook that calls a callback after a period of
 * time.
 *
 * Example
 *
 * ```
 * const App = () => {
 *   useTimer(5000, () => {
 *      console.log("5 second timer has expired!");
 *   });
 *
 *   return null;
 * };
 * ```
 */
declare const useTimer: (milliseconds: number, onTimeout: () => void) => void;
export default useTimer;
