/**
 * `useToggle` is a simple boolean toggle hook that returns a `boolean` and a
 * toggle function.
 *
 * Example
 *
 * ```
 * const App = () => {
 *   const [isChanged, setIsChanged] = useToggle();
 *
 *   return (
 *     <button onClick={setIsChanged}>
 *       {isChanged ? "Changed!" : "Click to Change"}
 *     </button>
 *   );
 * };
 * ```
 *
 * Credit: https://usehooks.com/useToggle/
 */
declare const useToggle: (initialState?: boolean) => [boolean, () => void];
export default useToggle;
